#include <stdio.h>
#include "libmfs.c"
#include <sys/select.h>

char buffer[BUFFER_SIZE] =" end";

void print_val(int val){
    printf("returned value: %d\n", val);
}

int
main(int argc, char *argv[])
{
    int ret;
	MFS_Stat_t stat;

	ret = MFS_Init("mumble-28.cs.wisc.edu", 5011);
    print_val(ret);
	ret = MFS_Lookup(0, "test");
    print_val(ret);
	ret = MFS_Stat(0, &stat);
    print_val(ret);
	ret = MFS_Write(0, (char*) &buffer, 1);
    print_val(ret);
	ret = MFS_Read(0, (char*) &buffer, 14);
    print_val(ret);
	ret = MFS_Creat(0, MFS_DIRECTORY, "test_filename");
    print_val(ret);
	ret = MFS_Unlink(0, "test_filename");
    print_val(ret);
	ret = MFS_Shutdown();
    print_val(ret);
    return 0;
}
